import { shaAsU8a } from "./asU8a.js";
/**
 * @name sha256AsU8a
 * @summary Creates a sha256 Uint8Array from the input.
 */

export function sha256AsU8a(value, onlyJs = false) {
  return shaAsU8a(value, 256, onlyJs);
}